import React, {Component} from 'react';
import axios from 'axios';
//import axios from 'axios'
//import { Link } from "react-router-dom";
//import { BrowserRouter as Router } from 'react-router-dom';

class EditPresenter extends Component {

  /*state = {
    id: this.props.presenter.data._id,
    presentername: this.props.presenter.data.presentername,
    evaluatorname: this.props.presenter.data.evaluatorname,
    topic:this.props.presenter.data.topic,
    articleurl:this.props.presenter.data.articleurl,
    presentationdat: this.props.presenter.data.presentationdat,
    textarea: this.props.presenter.data.textarea
  }*/



componentDidMount () {
    axios.get('/presenters').then((response) => {
        console.log('componentDidMount',this);
        this.setState({
            presenters:response.data
        })
    })
}

getPresenter = (id) => {

  let matchedPresenterIndex ;
  this.state.presenters.forEach((stud,index) => {
    if( stud._id === id){
      matchedPresenterIndex = index;
    }
  });
  const  machedStudData= this.state.presenters[matchedPresenterIndex];
  return {index:matchedPresenterIndex, data:machedStudData};
}

onEdit = (presenter,index) => {
  axios
  .put (`/presenters/${presenter._id}`, presenter)
  .then (response => {
   console.log ('I a m responding for edit',response.data);
   let newStdents = this.state.presenters;
   newStdents.splice(index,1,presenter);

   this.setState({
      presenters: newStdents,  
  });

 })
 .catch (err => console.log (err));
 //const updatedVal = this.props.presenters.map((dat,i) => {

}



  handleChange = (e) => {
      this.setState({
          [e.target.name]:e.target.value
      })
  }

  handleSubmit = (e) => {
      e.preventDefault();
      this.onEdit(this.state, this.presenter.index);
      console.log('benweree',this.presenter.index)
      this.props.history.push('/presenters');


  }


  render () {
    //console.log('wewe',this.state)

    return (


      <div className="form">
         <h3 className="addpresenter">Edit Presenter</h3>
        <form onSubmit = {this.handleSubmit} method="PUT">

        <div className="first-row">
          <label>Presenter Name:</label>
          <input type="text" 
          name="presentername" 
          value={this.state.presentername}
          onChange = {this.handleChange}
           />

           <label>Evaluator Name:</label>
           <input type="text" 
          name="evaluatorname" 
          ref="evaluatorname"
          value={this.state.evaluatorname}
          onChange = {this.handleChange}
           />

         <label>Presentation Topic:</label>
          <input type="text" 
          name="topic" 
          ref='topic'
          value={this.state.topic}
          onChange = {this.handleChange}
           />
        </div>

        <div className="second-row">
          <label>Article URI:</label>
          <input type="text" 
           name="articleurl" 
           ref='articleurl'
           value={this.state.articleurl}
           onChange = {this.handleChange}
           />

          <label>Presentation Date:</label>
          <input type="text" 
          name="presentationdat" 
          ref='presentationdat'
          value={this.state.presentationdat}
          onChange = {this.handleChange}
           />

        </div>

        <div className="first-column">
          <label>Key Words:</label>
          <input type="text" 
          name="keyWords" 
          ref='keyWords'
          value={this.state.keyWords}
          onChange = {this.handleChange}
           />

           <textarea
           type = "text"
           value={this.state.value}
            onChange={this.handleChange}
            rows="10"
            cols="60"
            name="message"
            placeholder="Write  Summary!"
          />
        </div>
        <br />

         <button className="editButton"> Update Presenter</button>  
        </form>


      </div>
    );
  }
}

export default EditPresenter;
