import React, { Component } from "react";
import axios from "axios";
import { Button, Form, FormGroup, Label, Input, FormText } from "reactstrap";
//import { Link } from "react-router-dom";
//import { BrowserRouter } from 'react-router-dom';

class AddPresenter extends Component {
  state = {
    presentername: "",
    evaluatorname: "",
    topic: "",
    articleurl: "",
    presentationdat: "",
    textarea: ""
  };

  handleChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  handleSubmit = e => {
    //console.log(this.state)
    e.preventDefault();
    //this.props.history.push('/presenters');
    //console.log (this.state);
    axios
      .post("/presenters", this.state)
      .then(response => {
        this.props.history.push("/presenters");
        console.log(this.props);
      })
      .catch(err => console.log(err));
  };

  render() {
    return (
      <Form onSubmit={this.handleSubmit} method="POST">

      <div className="form-container">
        <h3 style={{ marginTop: "30px" }} className="addpresenter">
          Add New Presenter
        </h3>

        <div className="first-row">
            <FormGroup>
              <Label htmlFor="presenterName">Presenter Name:</Label>
              <Input
                type="text"
                name="presentername"
                value={this.state.presentername}
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <Label htmlFor="evaluatorName">Evaluator Name:</Label>
              <Input
                type="text"
                name="evaluatorname"
                ref="evaluatorname"
                value={this.state.evaluatorname}
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <Label htmlFor="presentation">Presentation Topic:</Label>
              <Input
                type="text"
                name="topic"
                ref="topic"
                value={this.state.topic}
                onChange={this.handleChange}
              />    
            </FormGroup>



            <FormGroup>
                <Label htmlFor="articleUri">Article URI:</Label>
                <Input
                  type="text"
                  name="articleurl"
                  ref="articleurl"
                  value={this.state.articleurl}
                  onChange={this.handleChange}
                /> 
            </FormGroup>

            <FormGroup>
              <Label htmlFor="presentationDate">Presentation Date:</Label>
              <input
                type="date"
                name="presentationdat"
                ref="presentationdat"
                value={this.state.presentationdat}
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <Label htmlFor="keywords">Key Words:</Label>
              <input
                type="text"
                name="keyWords"
                ref="keyWords"
                value={this.state.keyWords}
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
            <Label for="exampleText">Text Area</Label>
            <Input
              type="textarea"
              value={this.state.value}
              onChange={this.handleChange}
              rows="10"
              cols="30"
              name="message"
              placeholder="Write  Summary!"
            />
            </FormGroup>

          <br />

          <Button> Add Presenter</Button>

           </div>
          </div>
         </Form>

    );
  }
}

export default AddPresenter;
