import React, { Component } from "react";
import axios from "axios";
//import axios from 'axios'
//import { Link } from "react-router-dom";
//import { BrowserRouter as Router } from 'react-router-dom';

class EditPresenter extends Component {
  state = {
    presenters: []
  };

  componentDidMount() {
    axios.get("/presenters" + this.props.match.params.id).then(response => {
      console.log("componentDidMount", response.data);
      this.setState({

    presentername: response.data.presentername,
    evaluatorname: response.data.evaluatorname,
    topic:response.data.topic,
    articleurl:response.data.articleurl,
    presentationdat: response.data.presentationdat,
    textarea: response.data.textarea
      });
    });
  }

  handleChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  handleSubmit = e => {
    e.preventDefault();
    this.props.onEdit(this.state, this.props.presenter.index);
    console.log("benweree", this.props.presenter.index);
    this.props.history.push("/presenters");
  };

  render() {
    console.log('wewe',this.state)

    return (
      <div className="form">
        <h3 className="addpresenter">Edit Presenter</h3>
        <form onSubmit={this.handleSubmit} method="PUT">
          <div className="first-row">
            <label>Presenter Name:</label>
            <input
              type="text"
              name="presentername"
              value={this.state.presentername}
              onChange={this.handleChange}
            />

            <label>Evaluator Name:</label>
            <input
              type="text"
              name="evaluatorname"
              ref="evaluatorname"
              value={this.state.evaluatorname}
              onChange={this.handleChange}
            />

            <label>Presentation Topic:</label>
            <input
              type="text"
              name="topic"
              ref="topic"
              value={this.state.topic}
              onChange={this.handleChange}
            />
          </div>

          <div className="second-row">
            <label>Article URI:</label>
            <input
              type="text"
              name="articleurl"
              ref="articleurl"
              value={this.state.articleurl}
              onChange={this.handleChange}
            />

            <label>Presentation Date:</label>
            <input
              type="text"
              name="presentationdat"
              ref="presentationdat"
              value={this.state.presentationdat}
              onChange={this.handleChange}
            />
          </div>

          <div className="first-column">
            <label>Key Words:</label>
            <input
              type="text"
              name="keyWords"
              ref="keyWords"
              value={this.state.keyWords}
              onChange={this.handleChange}
            />

            <textarea
              type="text"
              value={this.state.value}
              onChange={this.handleChange}
              rows="10"
              cols="60"
              name="message"
              placeholder="Write  Summary!"
            />
          </div>
          <br />

          <button className="editButton"> Update Presenter</button>
        </form>
      </div>
    );
  }
}

export default EditPresenter;
