import React, { Component } from "react";
import axios from "axios";
import { Button, Form, FormGroup, Label, Input, FormText } from "reactstrap";
//import { Link } from "react-router-dom";
//import { BrowserRouter } from 'react-router-dom';

class AddPresenter extends Component {
  state = {
    presentername: "",
    evaluatorname: "",
    topic: "",
    articleurl: "",
    presentationdat: "",
    textarea: ""
  };

  handleChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  handleSubmit = e => {
    //console.log(this.state)
    e.preventDefault();
    //this.props.history.push('/presenters');
    //console.log (this.state);
    axios
      .post("/presenters", this.state)
      .then(response => {
        this.props.history.push("/presenters");
        console.log(this.props);
      })
      .catch(err => console.log(err));
  };

  render() {
    return (
      <div className="form-group">
        <h3 style={{ marginTop: "30px" }} className="addpresenter">
          Add New Presenter
        </h3>
        <Form>
          <div className></div>
          <FormGroup>
            <Label for="exampleEmail">Presenter Name</Label>
            <Input
              type="text"
              name="presentername"
              id="exampleEmail"

            />
          </FormGroup>
          <FormGroup>
            <Label for="exampleEmail">Evaluator Name</Label>
            <Input
              type="text"
              name="evaluatorname"
              id="exampleEmail"

            />
          </FormGroup>
          <FormGroup>
            <Label for="exampleEmail">Presenattion Topic</Label>
            <Input
              type="text"
              name="topic"
              id="exampleEmail"

            />
          </FormGroup>
          <FormGroup>
            <Label for="examplePassword">Password</Label>
            <Input
              type="password"
              name="password"
              id="examplePassword"
              placeholder="password placeholder"
            />
          </FormGroup>

          <FormGroup>
            <Label for="exampleText">Text Area</Label>
            <Input type="textarea" name="text" id="exampleText" />
          </FormGroup>

          <Button>Submit</Button>
        </Form>
      </div>
    );
  }
}

export default AddPresenter;
